import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import InputAdornment from '@material-ui/core/InputAdornment';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import { makeStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { useSelector, useDispatch } from "react-redux";
import Actions from '../modules/actions'

const useStyles = makeStyles({
    root: {    
      textAlign: 'center'
    },
    cardContents: {
      textAlign: 'center',    
    }
  });

function Timer (props){
    const classes = useStyles();    
    const dispatch = useDispatch();

    const inputIntervalTime = (event) => {      
      const intervalTime = event.target.value.replace(/\D/g, '');
      const nameIndex = event.target.name.indexOf(':')
      const index = event.target.name.substring(0, nameIndex) * 1
      const timersId = event.target.name.substring(nameIndex + 1, event.target.name.length) * 1
      dispatch(Actions.updateIntervalTimer({intervalTime, index, timersId}))
    }

    return (
        <Card className={classes.cardContents}>
            <CardContent>
              <Typography variant="h5" component="h5">
                {props.timer.name}
              </Typography>
              <Table>
                <TableBody>
                  {props.timer.interval.map((t,i) => (
                    <TableRow key={i}>
                      <TableCell>{i}</TableCell>
                      <TableCell>
                        <TextField value={t} onChange={inputIntervalTime} name={props.timers.id + ":" + i}
                          InputProps={{
                            endAdornment: 
                            <InputAdornment position="end">秒</InputAdornment>
                        }}/>
                      </TableCell>
                  </TableRow>
                  ))}
                </TableBody>
              </Table>
              <Button>スタート</Button>
            </CardContent>
          </Card>          
        )
}

export default Timer
