// react
import { useParams } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from 'react'

// component
import Timer from './timer'
import TimerView from './timer_view'

import Grid from '@material-ui/core/Grid';  

function TimerDetail(){    
    const {Timer_id} = useParams()
    const state = useSelector(state => state)    
    const [timer, setTimer] = useState(state.timers.filter(t => t.id == Timer_id)[0])

    useEffect(()=>{      
      setTimer(state.timers.filter(t => t.id == Timer_id)[0])
    },[state.timers])

    return (
      <Grid container spacing={1}>
        <Grid item xs={4}>
          <Timer timer={timer} />
        </Grid>
        <Grid item xs={8}>
          <TimerView timer={timer}/>
        </Grid>
      </Grid>       
    )
}

export default TimerDetail
