import { useSelector, useDispatch } from "react-redux";
import Actions from './modules/actions'
import { useState } from "react";

// mui
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow'

// material ui のスタイルの指定https://qiita.com/uehaj/items/969ef20ccef850d2e9b1
// material ui gridの作成　https://qiita.com/vimyum/items/5ba06ca166ebe4992617
const useStyles = makeStyles({
  root: {    
    textAlign: 'center'
  },
  timerTitle: {
    textAlign: 'center',

  }
});

function App(){
  const dispatch = useDispatch();
  const state = useSelector(state => state)
  const [todo, setTodo] = useState("")
  const classes = useStyles();

  const getTodo = (e) => {
    setTodo(e.target.value)
  }

  const addTodo = () => {
    // dispatch(Actions.addTodo({todo}))    
  }

  return (
    <div>
      <Typography variant="h3" component="h3" className={classes.root}>
        タイマー作成サイト
      </Typography>
      <TextField label="タイマー名" variant="outlined"  onChange={getTodo} />
      <Button onClick={addTodo}>Add</Button>
      <Grid container spacing={1}>
        <Grid item xs={4}>
          <Card>
            <CardContent>
              <Typography variant="h5" component="h5" className={classes.timerTitle}>
                筋トレ
              </Typography>
              <Table>
                <TableBody>
                  <TableRow>
                    <TableCell>1</TableCell>
                    <TableCell>
                      <TextField defaultValue="10" />秒
                    </TableCell>
                  </TableRow>
                  <TableRow>
                    <TableCell>2</TableCell>
                    <TableCell>
                      <TextField defaultValue="30" />秒
                    </TableCell>
                  </TableRow>
                </TableBody>
              </Table>
            </CardContent>
          </Card>          
        </Grid>
        <Grid item xs={4}>
          <Card>
            <CardContent>
              ヤッホ2
            </CardContent>
          </Card>          
        </Grid>
      </Grid>      
    </div>
  )
}

export default App
