import { makeStyles } from '@material-ui/core/styles';
// component
import Timer from './timer'

// mui
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';

const useStyles = makeStyles({
    root: {    
      textAlign: 'center'
    },
    cardContents: {
      textAlign: 'center',      
    },
    waitTime: {
        color: '#AAA',
        backgroundColor: '#CCC',
        transition: '1s'
    },
    startTime: {

    },
  });

function TimerView(props){
    const classes = useStyles();    
    const state = useSelector(state => state)
    const [start, setStart] = useState(0)
    const getTimer = props.timer
    const getInterval = getTimer.interval

    const timerStart = () => {
      setStart(start + 1)
      timerCountDown()
    }

    const timerStop = () => {

    }

    const timerRestart = () => {

    }

    const timerReset = () => {

    }

    const timerCountDown = () => {
      if(getInterval[start] < 1){
        setStart(start + 1)
      }
      countDown()
    }

    const countDown = setInterval( ()=> {
        getInterval[start] -= 1
        console.log('a');
    },1000)

    useEffect(()=>{
      if(start > getInterval.length){
          setStart(1)
      }
    },[start])

    useEffect(()=>{
       console.log(getInterval); 
    },[])

    return (
        <div>
          {getInterval.map((t,i) => {
            i === start ?
            <Typography variant="h1" component="h1" key={i} className={classes.startTime}>
              {t}
            </Typography>
            :
            <Typography variant="h1" component="h1" key={i} className={classes.waitTime}>
              {t}
            </Typography>
            })}          
          <Typography variant="h5" component="h5" >
            あと{getTimer.loop}周
          </Typography>
          {start !== 0 ?                  
            <div>
              <Button>ストップ</Button>
              <Button>リスタート</Button>
              <Button>リセット</Button>
            </div>     
            :
            <div>
              <Button onClick={timerStart}>スタート</Button>
            </div>                   
          }                    
        </div>    
    )
}

export default TimerView
