// react
import { useParams } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import { useEffect } from 'react'

// component
import Timer from './timer'
import TimerView from './timer_view'

import Grid from '@material-ui/core/Grid';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    root: {    
      textAlign: 'center'
    },
    cardContents: {
      textAlign: 'center',      
    }
  });

function TimerDetail(){
    const classes = useStyles();
    const {Timer_id} = useParams()
    const state = useSelector(state => state)
    const getTimer = state.timers.filter(t => t.id == Timer_id)[0]    

    useEffect(()=>{
      console.log(state.timers);
    },[state.timers])

    return (
      <Grid container spacing={1}>
        <Grid item xs={4}>
          <Timer timer={getTimer} />
        </Grid>
        <Grid item xs={8}>
          <TimerView timer={getTimer}/>
        </Grid>
      </Grid>       
    )
}

export default TimerDetail
