// 初期State
const initialState = {
    users:
      { 
        uid:0,        
        timers:[
          {
            id:0,
            name:'筋トレ',            
            interval:[40,20,10]
          }
        ]
      }    
  }

const initialTimer = { 
            id:100,
            name:'',            
            interval:[]        
          }

  // Reducer処理
  const reducer = (state = initialState, action) => {
    switch (action.type) {
      case 'ADDTIMER': {
        const newTimer = Object.assign({},initialTimer)
        const newTimers = state.users.timers        
        newTimer.name = action.value.timerName  
        // idの付与
        newTimer.id = newTimers.reduce((a,c) => a.id > c.id , {id:0}) + 1
        newTimers.push(newTimer)             
        return { ...state, timers: newTimers }
      }      
      case 'UPDTODO': {
        return console.log(action.value)
      }
      case 'DELTODO': {
        return console.log(action.value)
      }
      case 'FETCHTODO': {
        return state
      }
      default: {
        return state
      }
    }
  }

  export default reducer
